import React, { Component } from 'react'
import 'antd/dist/antd.css'
import { List, Avatar } from 'antd'
import Header from "../../components/HeaderComponent/Header"


const listData = [];
for (let i = 0; i < 23; i++) {
    listData.push({
        href: 'https://ant.design',
        title: `Mã đơn : 13457982452${i}`,
        avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
        nameproduct: 'Bún chả, bun gan',
        money: '12345654',
        stateorder: 'chua giao',
        timedelivery: '123456789765',
        fromlocation: 'so 873, duong hoang quoc viet, co nhue ,bac tu liem , ha noi',
        tolocation: 'g234qgfqwef2   3',
    });
}

// const IconText = ({ icon, text }) => (
//     <Space>
//         {React.createElement(icon)}
//         {text}
//     </Space>
// );

export default class ListReciveOder extends Component {
    constructor(props){
        super(props);
        this.state = { data: []}
    }

    componentDidMount(){
        let config = {headers:{Auth: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhdXRob3JpemVkIjp0cnVlLCJleHAiOjE2MDg3ODQ4NzMsInR5cGUiOiIyIiwidHlwZV9pZCI6IjMifQ.p52hfb-czu67aGhK5nhm0a7K-HjTfnoToJx6xXzE02c'}}
        axios.get(`${STAT_URL}/v1/listorder`,config)
        .then((response) => {
            if (response.data.error.code === 200){
                this.setState({ data: response.data.data }, () => console.log(this.state.data))
            }
            else{
                this.props.history.push("/")
            } 
        })
        .catch(console.log)
    }

    render() {
        return (

            <div className="reciveorder">
                <Header></Header>
                <div id="label-order">
                    <h3>Đơn hàng đã nhận</h3>
                </div>
                <div className="container">
                <List
                    itemLayout="vertical"
                    size="large"
                    pagination={{
                        onChange: page => {
                            console.log(page);
                        },
                        pageSize: 6,
                    }}
                    dataSource={listData}

                    renderItem={item => (
                        <List.Item key={item.title} >
                            <List.Item.Meta
                                title={<a>Mã đơn:{item.id}</a>}
                            />
                            <div> Danh sách hàng:
                                {
                                (item.list_food || []).map((i) => 
                                    (<span>{i.name},</span>)
                                )}
                            </div>
                            <div className="total-bill">
                                <span>Tiền ứng hàng : </span>
                                <span>{item.total_amount}đ</span>
                            </div>
                            <div className="tolocation">
                                <span>Điểm đến: </span>
                                <span>{item.address}</span>
                            </div>
                            {item.order_status === 4 ? 
                            <div id="btn-nhan">
                            <button className="btn btn-danger" style={{ border: 'none' }}>Nhận Đơn</button>
                        </div> 
                        : <></>
                        }

                        </List.Item>
                    )}
                />
                </div>

            </div>
        )
    }
}
